class FormBase{constructor(place){this.place=place,this.listeners={},this.suspended=!1,this.title=""}getTitle(){return this.title}setSuspend(suspend){this.suspended!==suspend&&(this.suspended=suspend,this.suspended||this.publish("resume"))}isSuspended(){return this.suspended}publish(event,callback,error){const list=(this.listeners[event]||[]).slice();!function next(){list.length?list.pop()(next,error):void 0!==callback&&callback()}()}listen(event,handler){const list=this.listeners[event]||[];return this.listeners[event]=list,list.push(handler),()=>list.splice(list.indexOf(handler),1)}unlisten(handler){handler()}}export{FormBase as F};
